import React, { useState } from 'react';
import { Button } from './Button';
import { Link } from 'react-router-dom';
import './Navbar.css';
import Dropdown from './Dropdown';

function Navbar() {
  const [click, setClick] = useState(false);        //<17:22
  const [dropdown, setDropdown] = useState(false);  //26:50

  const handleClick = () => setClick(!click);       //17:22>
  const closeMobileMenu = () => setClick(false);    //<23:55

  const onMouseEnter = () => {
    if (window.innerWidth < 960) {
      setDropdown(false);
    } else {
      setDropdown(true);
    }
  };

  const onMouseLeave = () => {
    if (window.innerWidth < 960) {
      setDropdown(false);
    } else {
      setDropdown(false);
    }
  };

  return (
    <>
      {/*<18:00 */}
      <nav className='navbar'>
        <Link to='/' className='navbar-logo' onClick={closeMobileMenu}>
          EPIC
          <i class='fab fa-firstdraft' />
        </Link>
        <div className='menu-icon' onClick={handleClick}>
          <i className={click ? 'fas fa-times' : 'fas fa-bars'} />      {/*Saat rensponsive, berubah 3 garis tidur*/}
        </div>
        <ul className={click ? 'nav-menu active' : 'nav-menu'}>         {/*Saat 3 garis diklik, berubah jadi X*/}
          <li className='nav-item'>
            <Link to='/' className='nav-links' onClick={closeMobileMenu}> {/*Menu Home */}
              Home
            </Link>
          </li>
          <li
            className='nav-item'
            onMouseEnter={onMouseEnter}
            onMouseLeave={onMouseLeave}
          >
            <Link /*25:00*/
              to='/services'
              className='nav-links'
              onClick={closeMobileMenu}
            >
              Sign Up <i className='fas fa-caret-down' />    {/*Dropdown service*/}
            </Link>
            {dropdown && <Dropdown />}
          </li>
          {/* Menu producst */}
          <li className='nav-item'>
            <Link
              to='/products'
              className='nav-links'
              onClick={closeMobileMenu}
            >
              About
            </Link>
          </li>
          {/* Menu kontak */}
          <li className='nav-item'>
            <Link
              to='/contact-us'
              className='nav-links'
              onClick={closeMobileMenu}
            >
              Contact Us
            </Link>
          </li>
          <li
            className='nav-item'
            onMouseEnter={onMouseEnter}
            onMouseLeave={onMouseLeave}
          >
            <Link /*25:00*/
              to='/services'
              className='nav-links'
              onClick={closeMobileMenu}
            >
              Login <i className='fas fa-caret-down' />    {/*Dropdown service*/}
            </Link>
            {dropdown && <Dropdown />}
          </li>
        </ul>
        <Button />
      </nav>
    </>
  );
}

export default Navbar;
